const roteiro1 = $('setData').first().json.roteiro_1;
const roteiro2 = $('setData').first().json.roteiro_2;
const roteiro3 = $('setData').first().json.roteiro_3;

// Calcula o número de caracteres de cada roteiro
const comprimento1 = roteiro1.length;
const comprimento2 = roteiro2.length;
const comprimento3 = roteiro3.length;

// Soma os comprimentos e calcula a média
const mediaCaracteres = (comprimento1 + comprimento2 + comprimento3) / 3;

// Arredonda o resultado para um número inteiro (opcional, mas recomendado)
const mediaArredondada = Math.round(mediaCaracteres);

// Retorna o resultado. O n8n usará este objeto como a saída do nó.
return {
  json: {
    media_caracteres: mediaArredondada
  }
}

