// Acessa o primeiro item que chega ao nó.
const item = items[0];

// 1. Pega os dados dos nós anteriores de forma segura
const outputText = $input.first().json.output;
const mediaChars = $('setData3').first().json.media_chars;

// Verifica se os dados necessários existem antes de continuar
if (outputText === undefined || mediaChars === undefined) {
  throw new Error("Dados de 'output' ou 'media_chars' não encontrados. Verifique se os nós anteriores executaram corretamente.");
}

// 2. Faz a contagem de caracteres
const charCount = outputText.length;

// 3. Define a lógica de validação (+/- 12%)
// Converte mediaChars para número para garantir que o cálculo seja correto
const mediaCharsNum = parseFloat(mediaChars);
const lowerBound = mediaCharsNum * 0.70;
const upperBound = mediaCharsNum * 1.90;

// Verifica se a contagem está dentro do intervalo permitido
const isValid = (charCount >= lowerBound && charCount <= upperBound);

// 4. Estrutura o novo JSON de saída, substituindo os dados anteriores
item.json = {
  output: outputText,
  cont_chars: charCount,
  is_valid: isValid
};

// 5. Retorna o item com o novo JSON
return item;

